import React from 'react';
import './App.css';

class App extends React.Component {
  state = { open: false };

  onToggle = (event) => {
    this.setState({ open: true });
  }

  render() {
    return (
      <div>
        <button onClick={(event) => this.onToggle(event)}>
          click me!
        </button>
        { this.state.open && (
          <span> 1234 </span>
        )}
      </div>
    );
  }
}

export default App;
