import React from 'react';

class Tree extends React.Component {
  state = { open: false };

  onToggle = (event) => {
    this.setState({ open: !this.state.open });
  }

  render() {
    return (
      <div>
        <div onClick={(event) => this.onToggle(event)}>
          1
        </div>
        { this.state.open && (
          <div>
            123 
            <Tree onClick={(event) => this.onToggle(event)}/>
          </div>
        )}
      </div>
    );
  }
}

export default Tree;
