import React from 'react';


class Tree extends React.Component {
  state = { open: false };

  onToggle = (event) => {
    this.setState({ open: !this.state.open });
  }

  render() {
    const { members } = this.props; 
    return (
      <div>
        {members.map(member => (
          <button key={member.name} onClick={(event) => this.onToggle(event)}>
            {member.name}
          </button>
        ))}
        {this.state.open && (
            <div>{member.name}</div>
          )}
      </div>
    )
  }
}

export default Tree;
